# Application principale
class Application(Frame):
    def __init__(self, boss=None):
        Frame.__init__(self, borderwidth=5, bg='gray22', padx=20, pady=20, relief=GROOVE)
        self.master.title('PSYCHOTABS - Developed by CK - Nov. 2017')
        mBar=MenuBar(self)
        mBar.pack(side=TOP, fill=X, expand=1)
        # ScrollCanvas limite de la zone à parcourir avec la barre
        self.can=Canvas(self, width=1250, height=800, bg='snow3')
        self.frame = Frame(self.can)
        self.vsb = Scrollbar(self, orient=VERTICAL, command=self.can.yview)
        self.can.configure(yscrollcommand=self.vsb.set)
        self.vsb.pack(side=RIGHT, fill=Y)
        self.can.pack(side=LEFT, fill=BOTH, expand=YES)
        self.can.create_window((4,4), window=self.frame, anchor=NW, 
                                  tags="self.frame")
        # Insertion d'une image
        self.photo=PhotoImage(file='t1.gif')
        self.item=self.can.create_image(625, 400, image=self.photo)
        # Insertion du texte
        self.can.create_text(625, 500, anchor=CENTER, text="Python 3.5 - Tkinter 8.6 - GIMP 2.8",
                    font=('Times New Roman', 18), fill='aquamarine')
        self.can.create_text(170, 770, anchor=NE, text="Copyright (C) 2017 Inc.",
                    font=('Times', 12), fill='white') 
        self.can.pack(side=LEFT, fill=BOTH, expand=1)
        # Configuration de la Scrollbar sur le Frame
        self.frame.bind("<Configure>", self.onFrameConfigure)
        # Création de 4 boutons
        button1 = Button(self, text="CLICK ON IT!", font=70, bg='navy', fg='turquoise', anchor = CENTER, command = self.showonit)
        button1.configure(width=15, activebackground='SteelBlue', activeforeground='white', relief=GROOVE)
        button1_window = self.can.create_window(425, 600, anchor=CENTER, window=button1)

        button2 = Button(self, text="TUTORIAL", font=74, bg='navy', fg='turquoise', anchor = CENTER, command = self.showtuto)
        button2.configure(width=15, activebackground='SteelBlue', activeforeground='white', relief=GROOVE)
        button2_window = self.can.create_window(625, 600, anchor=CENTER, window=button2)

        button4 = Button(self, text="EQUIVALENCES", font=70, bg='navy', fg='turquoise', 
                         anchor = CENTER, command = self.showdico)
        button4.configure(width=15, activebackground='SteelBlue', activeforeground='white', relief=GROOVE)
        button4_window = self.can.create_window(825, 600, anchor=CENTER, window=button4)  

        self.pack()

    # Méthode pour reconfigurer la scrollbar à chaque fois
    def onFrameConfigure(self, event):
        '''Reset the scroll region to encompass the inner frame'''
        self.can.configure(scrollregion=self.can.bbox(ALL))

    def effacer(self):
        '''Réinitialise le canevas quand on passe d'un à l'autre'''
        self.can.delete(ALL)


    def page(self):
        self.can.delete(ALL)
        # Insertion d'une image
        self.photo=PhotoImage(file='t1.gif')
        self.item=self.can.create_image(625, 400, image=self.photo)
        # Insertion du texte
        self.can.create_text(625, 500, anchor=CENTER, text="Python 3.5 - Tkinter 8.6 - GIMP 2.8",
                    font=('Times New Roman', 18), fill='aquamarine')
        self.can.create_text(170, 770, anchor=NE, text="Copyright (C) 2017 Inc.",
                    font=('Times', 12), fill='white') 
        self.can.pack(side=LEFT, fill=BOTH, expand=1)
        # Configuration de la Scrollbar sur le Frame
        self.frame.bind("<Configure>", self.onFrameConfigure)
        # Création de 4 boutons
        button1 = Button(self, text="CLICK ON IT!", font=70, bg='navy', fg='turquoise', anchor = CENTER, command = self.showonit)
        button1.configure(width=15, activebackground='SteelBlue', activeforeground='white', relief=GROOVE)
        button1_window = self.can.create_window(425, 600, anchor=CENTER, window=button1)

        button2 = Button(self, text="TUTORIAL", font=74, bg='navy', fg='turquoise', anchor = CENTER, command = self.showtuto)
        button2.configure(width=15, activebackground='SteelBlue', activeforeground='white', relief=GROOVE)
        button2_window = self.can.create_window(625, 600, anchor=CENTER, window=button2)

        button4 = Button(self, text="EQUIVALENCES", font=70, bg='navy', fg='turquoise', 
                         anchor = CENTER, command = self.showdico)
        button4.configure(width=15, activebackground='SteelBlue', activeforeground='white', relief=GROOVE)
        button4_window = self.can.create_window(825, 600, anchor=CENTER, window=button4)       

        self.can.configure(scrollregion=self.can.bbox(ALL)) 

