    # Canevas réinitialisé, puis apparition des bouttons (Click on it!).
    def showonit(self):
        self.can.delete(ALL)
        self.photo=PhotoImage(file='neuro.gif')        
        self.item=self.can.create_image(625, 400, image=self.photo)

        self.x, self.y = 145, 40
        self.b=Button(self.can, width=25, font=16, bg='navy', fg='gold', 
                      activebackground='dark turquoise', 
                      activeforeground='black', 
                      text="Anti-épileptiques 1ère G", 
                      command=self.Ouverture)
